<?php/*Plugin Name:  VainCode Animate Scroll To TopPlugin URI: http://market.vaincode.com/product/animate-scroll-wordpress/Description: This plugin will add a scroll to top button with different Animation and speed. You can Customize From AAM Scroll Setting Page.Author: Md. Abdullah Al MahimAuthor URI: http://fb.com/a.a.mahimVersion: 1.1*//*-- Adding Latest jQuery from Wordpress --*/function aam_animation_scroll_latest_jquery() {	wp_enqueue_script('jquery');}add_action('init', 'aam_animation_scroll_latest_jquery');/*-- Some Set-up --*/define('AAM_SCROLL_HOOK', WP_PLUGIN_URL . '/' . plugin_basename( dirname(__FILE__) ) . '/' );/*-- Callback Animation Scroll JS Under main Jquery Library --*/wp_enqueue_script('aam-animation-scroll-main-jquery', AAM_SCROLL_HOOK.'js/animatescroll.js', array('jquery'));wp_enqueue_script('aam-animation-scroll-div-show-hide-on-scroll', AAM_SCROLL_HOOK.'js/ready.js', array('jquery'));/*-- Callback Colorpicker Javascript For Admin Panel --*/function aam_admin_colorpicker_js() {    wp_enqueue_script( 'colorpicker_sc', AAM_SCROLL_HOOK.'js/colorpicker.js');    wp_enqueue_script( 'eye_sc', AAM_SCROLL_HOOK.'js/eye.js' );    wp_enqueue_script( 'layout_sc', AAM_SCROLL_HOOK.'js/layout.js' );	    wp_enqueue_script( 'utils', AAM_SCROLL_HOOK.'js/utils.js' );}add_action( 'admin_head', 'aam_admin_colorpicker_js' );/*-- Enque Style's For aam animation scroll --*/wp_enqueue_style('aam-animation-scroll-custome-style', AAM_SCROLL_HOOK.'css/aam-scroll.css');/*-- Enque Style's For Font Awesome Icon's --*/wp_enqueue_style('aam-font-awesome-style', AAM_SCROLL_HOOK.'css/font-awesome.min.css');/*-- Enque Style's For Admin Panel --*/function aam_scroll_stylish_admin_plugin_styles() {    wp_enqueue_style( 'stylish-admin-panel-plugin-style', AAM_SCROLL_HOOK.'css/admin_css.css' );    wp_enqueue_style( 'stylish-admin-panel-colorpicker-style', AAM_SCROLL_HOOK.'css/colorpicker.css' );	wp_enqueue_style('aam-font-awesome-style', AAM_SCROLL_HOOK.'css/font-awesome.min.css');}add_action( 'admin_head', 'aam_scroll_stylish_admin_plugin_styles' );/*-- Add aam scroll menu into admin --*/function aam_scroll_setting_menu() {add_menu_page('VainCode Animate Scroll To Top Settings', 'AAM Scroll', 'administrator', __FILE__, 'aam_scroll_setting_sec');add_action( 'admin_init', 'register_scroll_inputs' );}add_action('admin_menu', 'aam_scroll_setting_menu');/*-- Register Custome Options For Control Panel Admin --*/function register_scroll_inputs() {	//register settings	register_setting( 'aam-settings-group', 'aam_scroll_section' );	register_setting( 'aam-settings-group', 'aam_scroll_text' );	register_setting( 'aam-settings-group', 'aam_scroll_animation_speed' );	register_setting( 'aam-settings-group', 'aam_scroll_style' );	register_setting( 'aam-settings-group', 'aam_scroll_position' );	register_setting( 'aam-settings-group', 'aam_scroll_bg_color' );	register_setting( 'aam-settings-group', 'aam_scroll_font_color' );	register_setting( 'aam-settings-group', 'custom_css' );	register_setting( 'aam-settings-group', 'scroll_icon' );	register_setting( 'aam-settings-group', 'aam_scroll_icon_size' );}function show_custom_css() {$custom_css 			= get_option('custom_css');?><style><?php echo $custom_css; ?></style><?php}add_action( 'wp_head', 'show_custom_css' );/*-- Scroll Content --*/function aam_scroll_content() {$aam_scroll_section 		= get_option('aam_scroll_section');$display_text 				= get_option('aam_scroll_text');$animation_speed 			= get_option('aam_scroll_animation_speed');$animation_style 			= get_option('aam_scroll_style');$scroll_position 			= get_option('aam_scroll_position');$scroll_background_color 	= get_option('aam_scroll_bg_color');$scroll_font_color 			= get_option('aam_scroll_font_color');$scroll_icon 				= get_option('scroll_icon');$aam_scroll_icon_size 		= get_option('aam_scroll_icon_size');/*-- Set Default Value --*/if($animation_style==""){?><div id="aam-scroll-div"  style="background:#ffffff;color:#000000;right:40px;" onclick="jQuery('body').animatescroll();"><span class="aam_scroll_icon"><i class="fa fa-angle-double-up fa-3x"></i></span>Top</div><?php}?>	   <?php if($animation_style=="normal") { ?>		<div id="aam-scroll-div"  style="background:#<?php echo $scroll_background_color; ?>;color:#<?php echo $scroll_font_color; ?>;<?php echo $scroll_position ?>:40px;" onclick="jQuery('<?php echo $aam_scroll_section; ?>').animatescroll();"><span class="aam_scroll_icon"><i class="fa <?php echo $scroll_icon; ?> fa-<?php echo $aam_scroll_icon_size; ?>"></i></span><?php echo $display_text; ?></div>		<?php } else { ?>		  		<div id="aam-scroll-div" style="background:#<?php echo $scroll_background_color; ?>;color:#<?php echo $scroll_font_color; ?>;<?php echo $scroll_position ?>:40px;" onclick="jQuery('<?php echo $aam_scroll_section; ?>').animatescroll({scrollSpeed:<?php echo $animation_speed; ?>,easing:'<?php echo $animation_style; ?>'});">		<span class="aam_scroll_icon"><i class="fa <?php echo $scroll_icon; ?> fa-<?php echo $aam_scroll_icon_size; ?>"></i></span>		<?php echo $display_text; ?></div>		<?php		}		?><?php}add_action( 'wp_footer', 'aam_scroll_content' );//Control Panel Admin Contentsfunction aam_scroll_setting_sec() {/*-- Saved Data's --*/$aam_scroll_section 		= get_option('aam_scroll_section');$display_text 				= get_option('aam_scroll_text');$animation_speed 			= get_option('aam_scroll_animation_speed');$animation_style 			= get_option('aam_scroll_style');$scroll_position 			= get_option('aam_scroll_position');$scroll_background_color 	= get_option('aam_scroll_bg_color');$scroll_font_color 			= get_option('aam_scroll_font_color');$scroll_icon 				= get_option('scroll_icon');$aam_scroll_icon_size 		= get_option('aam_scroll_icon_size');if($scroll_icon==""){ $scroll_icon="fa-angle-double-up"; }if($aam_scroll_icon_size==""){ $aam_scroll_icon_size="3x"; }?><div id="wrap"><h2 id="page_title">VainCode Animate Scroll To Top Setting Page</h2><form method="post" action="options.php" id="news_form">    <?php settings_fields( 'aam-settings-group' ); ?>    <?php do_settings_sections( 'aam-settings-group' ); ?>		<div>		<label for="input_box_source">Scroll Section</label>		<input id="input_box_source" type="text" name="aam_scroll_section" value="<?php if(get_option('aam_scroll_section')) : echo get_option('aam_scroll_section'); else : echo "body"; endif; ?>" required/>		<span class="description"><p>Enter The name of section for scroll. you can set any section of your webpage, just put the section class or id name here. (E.G: #main or .wraper) </p></span>	</div>		<div>		<label for="input_box_source">Display Text</label>		<input id="input_box_source" type="text" name="aam_scroll_text" value="<?php if(get_option('aam_scroll_text')) : echo get_option('aam_scroll_text'); else : echo "Back to Top"; endif; ?>" required/>		<span class="description"><p>Enter A Text Which one is display when user scroll Down</p></span>	</div>		<div>		<label for="animation_speed">Animation Speed</label>		<input id="animation_speed" type="text" name="aam_scroll_animation_speed" value="<?php if(get_option('aam_scroll_animation_speed')) : echo get_option('aam_scroll_animation_speed'); else : echo "2000"; endif; ?>" required/>		<span class="description"><p>Enter A Value For Scroll Animation, by Default its 2000.</p></span>	</div>		<div id="aam_scroll_icons">	<label><input type="radio" name="scroll_icon" value="fa-angle-double-up" <?php if($scroll_icon=="fa-angle-double-up"){ echo "checked='checked'";} ?>><i class="fa fa-angle-double-up fa-2x"></i></label>	<label><input type="radio" name="scroll_icon" value="fa-arrow-up" <?php if($scroll_icon=="fa-arrow-up"){ echo "checked='checked'";} ?>><i class="fa fa-arrow-up fa-2x"></i></label>	<label><input type="radio" name="scroll_icon" value="fa-chevron-circle-up" <?php if($scroll_icon=="fa-chevron-circle-up"){ echo "checked='checked'";} ?>><i class="fa fa-chevron-circle-up fa-2x"></i></label>	<label><input type="radio" name="scroll_icon" value="fa-angle-up" <?php if($scroll_icon=="fa-angle-up"){ echo "checked='checked'";} ?>><i class="fa  fa-angle-up fa-2x"></i></label>	<label><input type="radio" name="scroll_icon" value="fa-caret-up" <?php if($scroll_icon=="fa-caret-up"){ echo "checked='checked'";} ?>><i class="fa fa-caret-up fa-2x"></i></label>				<span class="description"><p>Select Scroll An Icon From Avobe</p></span>	</div>		<div>		<label for="aam_scroll_icon_size">Scroll Icon Size</label>		<select name="aam_scroll_icon_size" id="aam_scroll_icon_size">			<option value="lg" <?php if($aam_scroll_icon_size=="lg"){echo "Selected"; } ?>>1x</option>			<option value="2x" <?php if($aam_scroll_icon_size=="2x"){echo "Selected"; } ?>>2x</option>			<option value="3x" <?php if($aam_scroll_icon_size=="3x"){echo "Selected"; } ?>>3x</option>			<option value="4x" <?php if($aam_scroll_icon_size=="4x"){echo "Selected"; } ?>>4x</option>			<option value="5x" <?php if($aam_scroll_icon_size=="5x"){echo "Selected"; } ?>>5x</option>		</select>		<span class="description"><p>Select Scroll Icon Size to Display</p></span>	</div>		<div>		<label for="animation_style">Animation Style</label>		<select name="aam_scroll_style" id="animation_style">			<option value="normal" <?php if($animation_style=="normal"){echo "Selected"; } ?>>Normal</option>			<option value="easeInOutBack" <?php if($animation_style=="easeInOutBack"){echo "Selected"; } ?>>In Out Back</option>			<option value="easeOutBounce" <?php if($animation_style=="easeOutBounce"){echo "Selected"; } ?>>Out Bounce</option>			<option value="easeOutElastic" <?php if($animation_style=="easeOutElastic"){echo "Selected"; } ?>>Out Elastic</option>		</select>		<span class="description"><p>Select Scroll Animaion Style</p></span>	</div>	<div>		<label for="scroll_position">Scroll Position</label>		<select name="aam_scroll_position" id="scroll_position">			<option value="right" <?php if($scroll_position=="right"){echo "Selected"; } ?>>Bottom Right</option>			<option value="left"  <?php if($scroll_position=="left"){echo "Selected"; } ?>>Bottom Left</option>		</select>		<span class="description"><p>Select Scroll Display Position</p></span>	</div>	<div>		<label for="colorpickerField3">Scroll Background Color</label>		<input id="colorpickerField3" style="background:<?php echo "#".get_option('aam_scroll_bg_color'); ?>" type="text" name="aam_scroll_bg_color" value="<?php echo get_option('aam_scroll_bg_color'); ?>" required/>		<span class="description"><p>Select Scroll Background Color</p></span>	</div>	<div>		<label for="colorpickerField2">Scroll Font Color</label>		<input id="colorpickerField2" style="background:<?php echo "#".get_option('aam_scroll_font_color'); ?>" type="text" name="aam_scroll_font_color" value="<?php echo get_option('aam_scroll_font_color'); ?>" required />		<span class="description"><p>Select Scroll Font Color</p></span>	</div>	<div>		<label for="custom_css">Custom CSS</label>		<textarea id="custom_css" name="custom_css" rows="4" cols="40"><?php if(get_option('custom_css')) : echo get_option('custom_css'); else : echo "#aam-scroll-div{ }"; endif; ?></textarea>			<span class="description"><p>You Can Write Your Custom Css Style. These Css Style will Take place in Head Section on website.</p></span>	</div>		<center>		 <?php submit_button(); ?>	</center></form></div><?php } ?>